-- Australia
DROP VIEW IF EXISTS AustraliaView;
GO

CREATE VIEW AustraliaView AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlbankcustomerchurn.dfs.core.windows.net/gold/Bank_Churn/geography=Australia/',
    FORMAT='PARQUET'
) AS result;
GO

