DROP VIEW IF EXISTS GermanyView;
GO

CREATE VIEW GermanyView AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlintech001.dfs.core.windows.net/gold/Bank_Churn/geography=Germany/date_of_processing=2025-12-05/',
    FORMAT = 'PARQUET'
) AS result;
GO

DROP VIEW IF EXISTS FranceView;
GO

CREATE VIEW FranceView AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlintech001.dfs.core.windows.net/gold/Bank_Churn/geography=France/date_of_processing=2025-12-08/part-00000-0cbfed14-0c13-44d3-a650-894fbbd9e907.c000.snappy.parquet',
    FORMAT = 'PARQUET'
) AS result;
GO

