CREATE OR ALTER PROCEDURE usp_CreateCountryView
    @CountryName NVARCHAR(100)
AS
BEGIN
    DECLARE @SQL NVARCHAR(MAX);

    SET @SQL = '
    IF OBJECT_ID(''' + @CountryName + 'View'', ''V'') IS NOT NULL
        DROP VIEW ' + @CountryName + 'View;

    CREATE VIEW ' + @CountryName + 'View AS
    SELECT *
    FROM OPENROWSET(
        BULK ''https://dlbankcustomerchurn.dfs.core.windows.net/gold/Bank_Churn/geography=' + @CountryName + '/'' ,
        FORMAT = ''PARQUET''
    ) AS result;
    ';

    EXEC sp_executesql @SQL;
END;
