USE gold_db
GO

CREATE OR ALTER PROCEDURE dbo.usp_CreateCountryView
    @CountryName NVARCHAR(100)
AS
BEGIN
    DECLARE @SQL NVARCHAR(MAX);

    SET @SQL = N'
    CREATE OR ALTER VIEW ' + QUOTENAME(@CountryName + 'View') + N' AS
    SELECT *
    FROM OPENROWSET(
        BULK ''https://dlbankcustomerchurn.dfs.core.windows.net/gold/Bank_Churn/geography=' + @CountryName + N'/'',
        FORMAT = ''PARQUET''
    ) AS result;
    ';

    EXEC sp_executesql @SQL;
END;
GO
